import { Component, OnInit } from '@angular/core';
import { animate, state, style, transition, trigger } from '@angular/animations';

import { ToastService } from './toast.service';

@Component({
  selector: 'app-toast',
  templateUrl: './toast.component.html',
  styleUrls: ['./toast.component.scss'],
  animations: [
    trigger('toastTrigger', [
      state('open', style({ transform: 'translateY(0%)' })),
      state('close', style({ transform: 'translateY(-200%)' })),
      transition('open <=> close', [
        animate('300ms ease-in-out')
      ])
    ])
  ]
})
export class ToastComponent implements OnInit {
  toastClass!: string[];
  toastMessage!: string;
  toastIcon! : string;
  showsToast!: boolean;

  constructor(public toast: ToastService) { }

  ngOnInit(): void {
  }

  dismiss(): void {
    this.toast.dismissToast();
  }

}
<div
  [@toastTrigger]="(toast.showsToast$ | async) ? 'open' : 'close' "
  [class]="toast.toastState$ | async"
  class="toast"
>
  <div>
    <strong class="me-auto"><i class="{{ toast.toastIcon$ | async }}"></i></strong>
  </div>

  <div class="toast-body">
    {{ toast.toastMessage$ | async }}
  </div>

  <!--button-->
  <button  type="button" class="btn" (click)="this.dismiss()">
    <i class="fa-solid fa-xmark"></i>
  </button>
</div>
